'use strict';

Object.defineProperty(exports, Symbol.toStringTag, { value: 'Module' });

const react = require('@zag-js/react');
const tabs = require('@zag-js/tabs');
const React = require('react');
const environmentContext = require('../environment/environment-context.cjs');

const useTabs = (props) => {
  const getRootNode = environmentContext.useEnvironmentContext();
  const initialContext = {
    id: React.useId(),
    getRootNode,
    ...props,
    value: props.defaultValue
  };
  const context = {
    ...initialContext,
    value: props.value
  };
  const [state, send] = react.useMachine(tabs.machine(initialContext), { context });
  return tabs.connect(state, send, react.normalizeProps);
};

exports.useTabs = useTabs;
