'use strict';

Object.defineProperty(exports, Symbol.toStringTag, { value: 'Module' });

const domQuery = require('@zag-js/dom-query');

const dom = domQuery.createScope({
  getRootId: (ctx) => ctx.ids?.root ?? `checkbox:${ctx.id}`,
  getLabelId: (ctx) => ctx.ids?.label ?? `checkbox:${ctx.id}:label`,
  getControlId: (ctx) => ctx.ids?.control ?? `checkbox:${ctx.id}:control`,
  getInputId: (ctx) => ctx.ids?.input ?? `checkbox:${ctx.id}:input`,
  getRootEl: (ctx) => dom.queryById(ctx, dom.getRootId(ctx)),
  getInputEl: (ctx) => dom.queryById(ctx, dom.getInputId(ctx))
});

exports.dom = dom;
