'use strict';

Object.defineProperty(exports, Symbol.toStringTag, { value: 'Module' });

const fromPoints = require('./from-points.js');

const { min, max } = Math;
function union(...rs) {
  const pMin = {
    x: min.apply(
      Math,
      rs.map((r) => r.minX)
    ),
    y: min.apply(
      Math,
      rs.map((r) => r.minY)
    )
  };
  const pMax = {
    x: max.apply(
      Math,
      rs.map((r) => r.maxX)
    ),
    y: max.apply(
      Math,
      rs.map((r) => r.maxY)
    )
  };
  return fromPoints.getRectFromPoints(pMin, pMax);
}

exports.union = union;
