'use strict';

Object.defineProperty(exports, Symbol.toStringTag, { value: 'Module' });

const core = require('@zag-js/core');
const formUtils = require('@zag-js/form-utils');
const utils = require('@zag-js/utils');
const switch_dom = require('./switch.dom.js');

function machine(userContext) {
  const ctx = utils.compact(userContext);
  return core.createMachine(
    {
      id: "switch",
      initial: "ready",
      context: {
        checked: false,
        label: "switch",
        value: "on",
        ...ctx
      },
      watch: {
        disabled: "removeFocusIfNeeded",
        checked: ["invokeOnChange", "syncInputElement"]
      },
      activities: ["trackFormControlState"],
      on: {
        "CHECKED.TOGGLE": {
          actions: ["toggleChecked"]
        },
        "CHECKED.SET": {
          actions: ["setChecked"]
        },
        "DISPATCH.CHANGE": {
          actions: ["dispatchChangeEvent"]
        },
        "CONTEXT.SET": {
          actions: ["setContext"]
        }
      },
      states: {
        ready: {}
      }
    },
    {
      activities: {
        trackFormControlState(ctx2, _evt, { send, initialContext }) {
          return formUtils.trackFormControl(switch_dom.dom.getInputEl(ctx2), {
            onFieldsetDisabled() {
              ctx2.disabled = true;
            },
            onFormReset() {
              send({ type: "CHECKED.SET", checked: !!initialContext.checked });
            }
          });
        }
      },
      actions: {
        invokeOnChange(ctx2) {
          ctx2.onChange?.({ checked: ctx2.checked });
        },
        setContext(ctx2, evt) {
          Object.assign(ctx2, evt.context);
        },
        syncInputElement(ctx2) {
          const inputEl = switch_dom.dom.getInputEl(ctx2);
          inputEl.checked = !!ctx2.checked;
        },
        dispatchChangeEvent(ctx2, evt) {
          const inputEl = switch_dom.dom.getInputEl(ctx2);
          const checked = evt.checked;
          formUtils.dispatchInputCheckedEvent(inputEl, { checked, bubbles: true });
        },
        removeFocusIfNeeded(ctx2) {
          if (ctx2.disabled && ctx2.focused) {
            ctx2.focused = false;
          }
        },
        setChecked(ctx2, evt) {
          ctx2.checked = evt.checked;
        },
        toggleChecked(ctx2, _evt) {
          ctx2.checked = !ctx2.checked;
        }
      }
    }
  );
}

exports.machine = machine;
